%%sql
select * from fullJoin;

select * from customer_df I 
     full outer join 
     jdbcDF E on I.PROJECT_ID=E.PROJECT_ID;

%%sql

--ALL
SELECT * FROM FULLJOIN;

--No change records, ignore
select * from fullJoin WHERE LOWER(nk_hash) = LOWER(existing_nk_hash) and LOWER(column_hash) = LOWER(rec_checksum);

--INSERTS OR NEW ROWS
select COALESCE(MAX_KEY,0) + ROW_NUMBER () OVER (ORDER BY 1) AS CUSTOMER_KEY1, A.* from fullJoin A WHERE existing_nk_hash is null;

--Changed records or update old record and insert new with incremented version
select * from fullJoin WHERE WHERE LOWER(nk_hash) = LOWER(existing_nk_hash) and LOWER(column_hash) <> LOWER(rec_checksum);


--Soft deletes or rows no longer active in source
select * from fullJoin WHERE WHERE nk_hash is null;

--INSERT INTO DBXDH.DHT_CUSTOMER1 
select * from fullJoin where existing_nk_hash is null;
